// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { socket } from './socket';
import Home from './components/Home';
import Lobby from './components/Lobby';
import GameRoom from './components/GameRoom';

function App() {
  const [playerName, setPlayerName] = useState('');
  const [gameCode, setGameCode] = useState('');
  const [game, setGame] = useState(null); // Состояние текущей игры
  const [characterOnForehead, setCharacterOnForehead] = useState(null); // Персонаж на лбу у текущего игрока
  const [error, setError] = useState('');

  const navigate = useNavigate(); // Используем useNavigate внутри Router

  useEffect(() => {
    // Обработчики событий Socket.IO
    socket.on('connect', () => {
      console.log('Подключено к серверу Socket.IO');
      setError(''); // Сбрасываем ошибку при успешном подключении
    });

    socket.on('disconnect', () => {
      console.log('Отключено от сервера Socket.IO');
      setGame(null);
      setCharacterOnForehead(null);
      setGameCode('');
      setError('Вы были отключены от сервера. Возможно, игра завершилась или сервер перезагрузился.');
      navigate('/'); // Возвращаемся на главную
    });

    socket.on('gameCreated', (code) => {
      setGameCode(code);
      navigate(`/lobby/${code}`);
    });

    socket.on('gameUpdate', (updatedGame) => {
      setGame(updatedGame);
      setError(''); // Сбрасываем ошибку при получении обновления
    });

    socket.on('characterAssigned', (character) => {
      setCharacterOnForehead(character);
    });

    socket.on('gameError', (message) => {
      setError(message);
      console.error('Ошибка игры:', message);
    });

    socket.on('gameEnded', (message) => {
      setError(message);
      setGame(null);
      setCharacterOnForehead(null);
      setGameCode('');
      navigate('/');
    });

    // Очистка обработчиков при размонтировании компонента
    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('gameCreated');
      socket.off('gameUpdate');
      socket.off('characterAssigned');
      socket.off('gameError');
      socket.off('gameEnded');
    };
  }, [navigate]); // Добавляем navigate в зависимости

  // Функции для взаимодействия с сервером
  const handleCreateGame = (name) => {
    if (!socket.connected) socket.connect();
    setPlayerName(name);
    socket.emit('createGame', name);
  };

  const handleJoinGame = (code, name) => {
    if (!socket.connected) socket.connect();
    setPlayerName(name);
    setGameCode(code);
    socket.emit('joinGame', code, name);
    navigate(`/lobby/${code}`);
  };

  return (
    <div className="app-container">
      {error && <div className="error-message">{error}</div>}
      <Routes>
        <Route path="/" element={<Home onCreateGame={handleCreateGame} onJoinGame={handleJoinGame} />} />
        <Route
          path="/lobby/:gameCode"
          element={
            <Lobby
              game={game}
              playerName={playerName}
              socketId={socket.id}
              onStartGame={() => socket.emit('startGame', gameCode)}
              onSubmitCharacter={(char) => socket.emit('submitCharacter', gameCode, char)}
              onChatMessage={(msg) => socket.emit('chatMessage', gameCode, msg)}
            />
          }
        />
        <Route
          path="/game/:gameCode"
          element={
            <GameRoom
              game={game}
              playerName={playerName}
              socketId={socket.id}
              characterOnForehead={characterOnForehead}
              onAskQuestion={(q) => socket.emit('askQuestion', gameCode, q)}
              onAnswerQuestion={(a) => socket.emit('answerQuestion', gameCode, a)}
              onMakeGuess={(g) => socket.emit('makeGuess', gameCode, g)}
              onChatMessage={(msg) => socket.emit('chatMessage', gameCode, msg)}
            />
          }
        />
      </Routes>
    </div>
  );
}

// Оборачиваем App в Router, чтобы useNavigate работал
export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
