// frontend/src/components/GameRoom.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import PlayerCard from './PlayerCard';
import Chat from './Chat';

function GameRoom({ game, playerName, socketId, characterOnForehead, onAskQuestion, onAnswerQuestion, onMakeGuess, onChatMessage }) {
  const [questionInput, setQuestionInput] = useState('');
  const [guessInput, setGuessInput] = useState('');
  const navigate = useNavigate();
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (!game || game.status === 'waiting' || game.status === 'finished') {
      navigate(`/lobby/${game?.code || ''}`); // Если игра не началась или закончилась, возвращаем в лобби
    }
    // Прокрутка чата вниз при новых сообщениях
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [game, navigate]);

  if (!game) {
    return <div className="loading-screen">Загрузка игровой комнаты...</div>;
  }

  const currentPlayer = game.players.find(p => p.id === game.players[game.currentPlayerIndex]?.id);
  const isMyTurn = currentPlayer && currentPlayer.id === socketId;
  const myPlayer = game.players.find(p => p.id === socketId);
  const hasGuessed = myPlayer?.guessed;

  const handleAskQuestion = () => {
    if (questionInput.trim() && isMyTurn && !hasGuessed) {
      onAskQuestion(questionInput.trim());
      setQuestionInput('');
    }
  };

  const handleMakeGuess = () => {
    if (guessInput.trim() && isMyTurn && !hasGuessed) {
      onMakeGuess(guessInput.trim());
      setGuessInput('');
    }
  };

  const handleAnswer = (answer) => {
    onAnswerQuestion(answer);
  };

  const handleChatMessage = (message) => {
    if (message.trim()) {
      onChatMessage(message.trim());
    }
  };

  return (
    <div className="game-room-container glass-panel">
      <h1 className="game-room-title">Игра: {game.code}</h1>
      <p className="game-status">Статус: {game.status === 'in-progress' ? 'В процессе' : 'Завершена'}</p>

      <div className="game-board">
        <div className="players-display">
          {game.players.map((player) => (
            <PlayerCard
              key={player.id}
              player={player}
              isCurrentPlayer={player.id === socketId}
              isTurn={player.id === currentPlayer?.id}
              characterOnForehead={player.id === socketId ? characterOnForehead : null} // Показываем только свой
            />
          ))}
        </div>

        <div className="game-controls">
          {game.status === 'in-progress' && !hasGuessed && (
            <>
              <div className="current-turn-info">
                <p>Сейчас ход: <span className="highlight">{currentPlayer?.name}</span></p>
                {isMyTurn && <p className="my-turn-message">Это ваш ход!</p>}
              </div>

              {isMyTurn && !hasGuessed && (
                <div className="action-section">
                  <h3>Ваш персонаж: <span className="character-on-forehead">{characterOnForehead || 'Загрузка...'}</span></h3>
                  <p>Осталось вопросов за ход: {game.maxQuestionsPerTurn - (myPlayer?.questionsAskedInTurn || 0)}</p>
                  <div className="input-group">
                    <input
                      type="text"
                      value={questionInput}
                      onChange={(e) => setQuestionInput(e.target.value)}
                      placeholder="Задайте вопрос (например: Я человек?)"
                      disabled={myPlayer?.questionsAskedInTurn >= game.maxQuestionsPerTurn}
                      maxLength="100"
                    />
                    <button onClick={handleAskQuestion} className="btn primary" disabled={myPlayer?.questionsAskedInTurn >= game.maxQuestionsPerTurn}>
                      Спросить
                    </button>
                  </div>
                  <div className="input-group">
                    <input
                      type="text"
                      value={guessInput}
                      onChange={(e) => setGuessInput(e.target.value)}
                      placeholder="Попытка угадать (например: Бэтмен)"
                      maxLength="30"
                    />
                    <button onClick={handleMakeGuess} className="btn secondary">
                      Угадать!
                    </button>
                  </div>
                </div>
              )}

              {!isMyTurn && (
                <div className="answer-section">
                  <h3>Ответьте на вопрос:</h3>
                  <p>Последний вопрос: "{game.chatMessages[game.chatMessages.length - 1]?.message}"</p>
                  <button onClick={() => handleAnswer(true)} className="btn success">Да</button>
                  <button onClick={() => handleAnswer(false)} className="btn danger">Нет</button>
                </div>
              )}
            </>
          )}

          {hasGuessed && <p className="guessed-message">Вы уже угадали своего персонажа!</p>}
          {game.status === 'finished' && (
            <div className="game-over-section">
              <h2>Игра завершена!</h2>
              <p>Победитель: {game.players.find(p => p.guessed)?.name || 'Неизвестно'}</p>
              <h3>Персонажи:</h3>
              <ul>
                {game.players.map(p => (
                  <li key={p.id}>{p.name}: {p.characterOnForehead}</li>
                ))}
              </ul>
              <button onClick={() => navigate('/')} className="btn primary">На главную</button>
            </div>
          )}
        </div>
      </div>

      <div className="chat-container glass-panel">
  <Chat messages={game.chatMessages} onSendMessage={handleChatMessage} chatEndRef={chatEndRef} />
</div>
    </div>
  );
}

export default GameRoom;
