// frontend/src/components/PlayerCard.js
import React from 'react';

function PlayerCard({ player, isCurrentPlayer, isTurn, characterOnForehead }) {
  const cardClass = `player-card ${isCurrentPlayer ? 'current-player' : ''} ${isTurn ? 'active-turn' : ''} ${player.guessed ? 'guessed' : ''}`;

  return (
    <div className={cardClass}>
      <div className="player-avatar"></div> {/* Placeholder for futuristic avatar */}
      <h3 className="player-name">{player.name}</h3>
      {isCurrentPlayer && characterOnForehead && (
        <div className="character-display">
          <span className="label">На лбу:</span>
          <span className="character-name">{characterOnForehead}</span>
        </div>
      )}
      {!isCurrentPlayer && player.characterSubmitted && (
        <div className="character-display">
          <span className="label">Загадал:</span>
          <span className="character-name">{player.characterSubmitted}</span>
        </div>
      )}
      {player.guessed && <span className="guessed-badge">Угадал!</span>}
    </div>
  );
}

export default PlayerCard;
