// frontend/src/components/PlayerCard.js
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckCircle } from '@fortawesome/free-solid-svg-icons';

// Импортируем только те аватарки, которые у нас есть
import avatar1 from '../assets/images/avatar1.png';
import avatar2 from '../assets/images/avatar2.png';
import avatar3 from '../assets/images/avatar3.png';
// Я удалил импорт avatar4.png

// В этом массиве теперь тоже только 3 аватарки
const avatars = [avatar1, avatar2, avatar3];

// Функция для получения уникального аватара для каждого игрока
const getAvatarForPlayer = (playerId) => {
  // Преобразуем ID сокета в число, чтобы получить стабильный индекс
  let hash = 0;
  for (let i = 0; i < playerId.length; i++) {
    hash = playerId.charCodeAt(i) + ((hash << 5) - hash);
  }
  const index = Math.abs(hash % avatars.length);
  return avatars[index];
};

function PlayerCard({ player, isCurrentPlayer, isTurn, characterOnForehead }) {
  const cardClass = `player-card ${isCurrentPlayer ? 'current-player' : ''} ${isTurn ? 'active-turn' : ''} ${player.guessed ? 'guessed' : ''}`;
  const playerAvatar = getAvatarForPlayer(player.id);

  return (
    <div className={cardClass}>
      <div className="player-avatar">
        <img src={playerAvatar} alt="Player Avatar" />
      </div>
      <div className="player-name-container">
        <h3 className="player-name">{player.name}</h3>
        {player.characterSubmitted && !player.guessed && (
          <FontAwesomeIcon icon={faCheckCircle} className="ready-icon" title="Персонаж загадан" />
        )}
      </div>
      {isCurrentPlayer && characterOnForehead && (
        <div className="character-display glass-panel" style={{ padding: '10px', marginTop: '10px' }}>
          <span className="label">На лбу:</span>
          <span className="character-name" style={{ fontWeight: '700', color: 'var(--secondary-color)' }}>
            {characterOnForehead}
          </span>
        </div>
      )}
      {player.guessed && <span className="guessed-badge">Угадал!</span>}
    </div>
  );
}

export default PlayerCard;
