// frontend/src/components/Lobby.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import PlayerCard from './PlayerCard';
import Chat from './Chat';

function Lobby({ game, playerName, socketId, onStartGame, onSubmitCharacter }) {
  const [characterInput, setCharacterInput] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (game && game.status === 'in-progress') {
      navigate(`/game/${game.code}`);
    }
  }, [game, navigate]);

  if (!game) {
    return <div className="loading-screen">Загрузка лобби...</div>;
  }

  const isHost = game.hostId === socketId;
  const currentPlayer = game.players.find(p => p.id === socketId);
  const hasSubmittedCharacter = currentPlayer && currentPlayer.characterSubmitted !== null;
  const allPlayersSubmitted = game.players.every(p => p.characterSubmitted !== null);

  return (
    <div className="lobby-container">
      <h1 className="lobby-title">Лобби игры: {game.code}</h1>
      <p className="lobby-info">Вы: {playerName} {isHost && '(Хост)'}</p>

      <div className="lobby-content">
        <div className="players-section">
          <h2>Игроки ({game.players.length})</h2>
          <div className="player-list">
            {game.players.map((player) => (
              <PlayerCard key={player.id} player={player} isCurrentPlayer={player.id === socketId} />
            ))}
          </div>
        </div>

        <div className="character-submission-section">
          <h2>Загадайте персонажа</h2>
          {!hasSubmittedCharacter ? (
            <div className="input-group">
              <input
                type="text"
                value={characterInput}
                onChange={(e) => setCharacterInput(e.target.value)}
                placeholder="Например: Бэтмен, Гарри Поттер"
                maxLength="30"
              />
              <button onClick={() => onSubmitCharacter(characterInput)} className="btn primary">
                Загадать
              </button>
            </div>
          ) : (
            <p className="submitted-message">Вы загадали: <strong>{currentPlayer.characterSubmitted}</strong></p>
          )}
          {!allPlayersSubmitted && <p className="waiting-message">Ожидаем, пока все игроки загадают персонажей...</p>}
        </div>

        {isHost && allPlayersSubmitted && game.players.length >= 2 && (
          <div className="start-game-section">
            <button onClick={onStartGame} className="btn success">
              Начать игру
            </button>
          </div>
        )}
      </div>

      <Chat messages={game.chatMessages} />
    </div>
  );
}

export default Lobby;
